// src/components/VideoTutorials.js
import React, { useState } from 'react';

const VideoTutorials = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [currentVideo, setCurrentVideo] = useState('');

  const videos = [
    {
      title: "Introduction to Accessibility",
      url: "https://www.youtube.com/embed/dQw4w9WgXcQ", // Replace with the actual embed URL
      thumbnail: "https://via.placeholder.com/150" // Replace with the actual thumbnail URL
    },
    {
      title: "Color Contrast Basics",
      url: "https://www.youtube.com/embed/dQw4w9WgXcQ", // Replace with the actual embed URL
      thumbnail: "https://via.placeholder.com/150" // Replace with the actual thumbnail URL
    },
  ];

  const openModal = (url) => {
    setCurrentVideo(url);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setCurrentVideo('');
  };

  return (
    <section>
      <h2>Video Tutorials</h2>
      <div className="video-thumbnails">
        {videos.map((video, index) => (
          <div key={index} className="video-thumbnail" onClick={() => openModal(video.url)}>
            <img src={video.thumbnail} alt={video.title} />
            <h3>{video.title}</h3>
          </div>
        ))}
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <iframe
              width="560"
              height="315"
              src={currentVideo}
              title="Video Player"
              frameBorder="0"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      )}
    </section>
  );
};

export default VideoTutorials;
