import React, { useEffect, useState } from 'react';
import './stCuriosityCorner.css';

const facts = [
  "1 in 5 people in the world live with a disability.",
  "Accessibility can improve usability for all users.",
  "Color blindness affects approximately 1 in 12 men and 1 in 200 women.",
];

const CuriosityCorner = () => {
  const [randomFact, setRandomFact] = useState('');

  useEffect(() => {
    generateRandomFact();
  }, []);

  const generateRandomFact = () => {
    const fact = facts[Math.floor(Math.random() * facts.length)];
    setRandomFact(fact);
    playSound();
  };

  const playSound = () => {
    const audio = new Audio('https://example.com/pop-sound.mp3'); // Replace with a sound URL
    audio.play();
  };

  return (
    <section className="curiosity-corner">
      <div className="globe-container" onClick={generateRandomFact}>
        <div className="globe"></div>
        <div className="bubble">{randomFact}</div>
      </div>
      <h2>Curiosity Corner</h2>
      <button className="fact-button" onClick={generateRandomFact}>Show Another Fact!</button>
    </section>
  );
};

export default CuriosityCorner;
