import React, { useState } from 'react';
import './styles/ResourceLibrary.css'; // Import the CSS file

const resources = [
  {
    "title": "Web Content Accessibility Guidelines (WCAG)",
    "url": "https://www.w3.org/WAI/WCAG21/quickref/",
    "description": "A comprehensive guide to making web content accessible to people with disabilities."
  },
  {
    "title": "Color Contrast Checker",
    "url": "https://webaim.org/resources/contrastchecker/",
    "description": "A tool to check the contrast ratio between text and background colors to ensure readability."
  },
  {
    "title": "A11Y Project",
    "url": "https://a11yproject.com/",
    "description": "A community-driven project to make digital accessibility easier for everyone."
  },
  {
    "title": "WebAIM",
    "url": "https://webaim.org/",
    "description": "Resources and tools for web accessibility, including training and evaluation services."
  },
  {
    "title": "WAVE Web Accessibility Evaluation Tool",
    "url": "https://wave.webaim.org/",
    "description": "A web accessibility evaluation tool that helps identify accessibility and Web Content Accessibility Guidelines (WCAG) issues."
  },
  {
    "title": "Accessibility Checker",
    "url": "https://www.accessibilitychecker.org/",
    "description": "An online tool to check the accessibility of your web content."
  },
  {
    "title": "Accessible Rich Internet Applications (ARIA)",
    "url": "https://www.w3.org/WAI/ARIA/",
    "description": "A set of attributes that define ways to make web content and applications more accessible."
  },
  {
    "title": "Color Contrast Analyzer",
    "url": "https://developer.paciellogroup.com/resources/contrastanalyser/",
    "description": "A desktop application that allows you to analyze the color contrast of text and background."
  },
  {
    "title": "Axe Accessibility Checker",
    "url": "https://www.deque.com/axe/",
    "description": "A suite of accessibility testing tools that help developers find and fix accessibility issues."
  },
  {
    "title": "Google Accessibility Developer Guide",
    "url": "https://developers.google.com/web/fundamentals/accessibility",
    "description": "Guidelines and best practices for building accessible web applications."
  },
  {
    "title": "Accessibility Insights",
    "url": "https://accessibilityinsights.io/",
    "description": "Tools for finding and fixing accessibility issues in your web applications."
  },
  {
    "title": "Web Accessibility Initiative (WAI)",
    "url": "https://www.w3.org/WAI/",
    "description": "A comprehensive resource for web accessibility standards and guidelines."
  } // <-- Missing closing bracket here
]; // Closing the resources array

const ResourceLibrary = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6; // Adjust as needed

  const filteredResources = resources.filter(resource =>
    resource.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredResources.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentItems = filteredResources.slice(startIndex, startIndex + itemsPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <section className="section">
      <h2 className="header">🌟 Resource Library 🌟</h2>
      <input
        type="text"
        placeholder="Search resources..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="searchInput"
      />
      <ul className="list">
        {currentItems.map(resource => (
          <li key={resource.title} className="listItem">
            <a 
              href={resource.url}
              target="_blank"
              rel="noopener noreferrer"
              className="link"
            >
              {resource.title}
            </a>
            <p className="description">{resource.description}</p>
          </li>
        ))}
      </ul>
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => (
          <button 
            key={index + 1} 
            onClick={() => handlePageChange(index + 1)}
            disabled={currentPage === index + 1}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </section>
  );
};

export default ResourceLibrary;
