// frontend/src/components/UserFeedback.js

import React, { useState } from 'react';
import './styles/UserFeedback.css';

const UserFeedback = () => {
  const [feedback, setFeedback] = useState('');
  const [rating, setRating] = useState(0);
  const [category, setCategory] = useState('');
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);

  const handleSubmit = async () => {
    if (!feedback) {
      alert("Please provide your feedback.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/sentiment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ feedback }),
      });

      if (!res.ok) {
        console.error("Server returned status:", res.status);
        alert("Error from sentiment endpoint.");
        return;
      }

      const { score, label } = await res.json();
      alert(`Sentiment: ${label} (score ${score.toFixed(2)})`);

      // Optionally, here you could POST rating/category/feedback to another endpoint
      // to store in a database. This example just runs sentiment.

      // Show “Feedback submitted successfully” popup
      setShowSuccessPopup(true);
      setTimeout(() => setShowSuccessPopup(false), 3000);

      // Clear form fields
      setFeedback('');
      setRating(0);
      setCategory('');
    } catch (err) {
      console.error("Fetch error:", err);
      alert("Could not connect to the sentiment service.");
    }
  };

  return (
    <section className="feedback-section">
      <h2>User Feedback</h2>

      <div>
        <label>Category:</label>
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Select a category</option>
          <option value="Suggestion">Suggestion</option>
          <option value="Bug Report">Bug Report</option>
          <option value="Compliment">Compliment</option>
        </select>
      </div>

      <div className="rating">
        <span>Rate your experience: </span>
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={`star ${rating >= star ? 'filled' : ''}`}
            onClick={() => setRating(star)}
            style={{ cursor: "pointer", fontSize: "1.5rem", color: rating >= star ? "#FFD700" : "#CCC" }}
          >
            ★
          </span>
        ))}
      </div>

      <textarea
        value={feedback}
        onChange={(e) => setFeedback(e.target.value)}
        placeholder="Share your feedback..."
        rows={4}
        style={{ width: "100%", padding: "0.5rem", resize: "vertical" }}
      />

      <button 
        onClick={handleSubmit} 
        style={{ 
          marginTop: "0.75rem", 
          padding: "0.5rem 1rem", 
          backgroundColor: "#28a745", 
          color: "#fff", 
          border: "none", 
          borderRadius: "4px", 
          cursor: "pointer" 
        }}
      >
        Submit Feedback
      </button>

      {showSuccessPopup && (
        <div className="success-popup" style={{
            marginTop: "1rem",
            padding: "0.5rem 1rem",
            backgroundColor: "#d4edda",
            border: "1px solid #c3e6cb",
            borderRadius: "4px",
            color: "#155724"
          }}>
          <p>Feedback submitted successfully!</p>
        </div>
      )}
    </section>
  );
};

export default UserFeedback;
