import React, { useState } from 'react';
import './App.css';
import Checklist from './components/Checklist';
import ResourceLibrary from './components/ResourceLibrary';
import ColorContrastAnalyzer from './components/ColorContrastAnalyzer';
import FontSizeAdjuster from './components/FontSizeAdjuster';
import KeyboardNavigator from './components/KeyboardNavigator';
import CuriosityCorner from './components/CuriosityCorner';
import VideoTutorials from './components/VideoTutorials';
import UserFeedback from './components/UserFeedback';
import DiscussionForum from './components/DiscussionForum';
import Achievements from './components/Achievements';
import Webinars from './components/Webinars';

function App() {
  const [activeTab, setActiveTab] = useState('checklist');
  const [darkMode, setDarkMode] = useState(false);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'checklist':
        return <Checklist />;
      case 'resources':
        return <ResourceLibrary />;
      case 'colorContrast':
        return <ColorContrastAnalyzer />;
      case 'fontSize':
        return <FontSizeAdjuster />;
      case 'keyboardNavigator':
        return <KeyboardNavigator />;
      case 'curiosity':
        return <CuriosityCorner />;
      case 'videoTutorials':
        return <VideoTutorials />;
      case 'userFeedback':
        return <UserFeedback />;
      case 'discussionForum':
        return <DiscussionForum />;
      case 'achievements':
        return <Achievements />;
      case 'webinars':
        return <Webinars />;
      default:
        return <Checklist />;
    }
  };

  return (
    <div className={`App ${darkMode ? 'dark' : 'light'}`}>
      <header>
        <h1>Community-Driven Accessibility Toolkit</h1>
      </header>
      <div className="dashboard">
        <nav className="sidebar">
          <ul>
            <li onClick={() => setActiveTab('colorContrast')}>Color Contrast Analyzer</li>
            <li onClick={() => setActiveTab('fontSize')}>Font Size Adjuster</li>
            <li onClick={() => setActiveTab('keyboardNavigator')}>Keyboard Navigator</li>
            <li onClick={() => setActiveTab('checklist')}>MyChecklist</li>
            <li onClick={() => setActiveTab('resources')}>Resource Library</li>
            <li onClick={() => setActiveTab('curiosity')}>Curiosity Corner</li>
            <li onClick={() => setActiveTab('videoTutorials')}>Video Tutorials</li>
            <li onClick={() => setActiveTab('userFeedback')}>User Feedback</li>
            <li onClick={() => setActiveTab('discussionForum')}>Discussion Forum</li>
            <li onClick={() => setActiveTab('achievements')}>Achievements</li>
            <li onClick={() => setActiveTab('webinars')}>Webinars</li>
          </ul>
          <button onClick={toggleTheme} className="theme-toggle">
            Switch to {darkMode ? 'Light' : 'Dark'} Mode
          </button>
        </nav>
        <main className="content">
          {renderTabContent()}
        </main>
      </div>
    </div>
  );
}

export default App;
