import React, { useState } from 'react';
import './styles/ColorCo'

const ColorContrastAnalyzer = () => {
  const [foreground, setForeground] = useState('#000000');
  const [background, setBackground] = useState('#FFFFFF');
  const contrastRatio = calculateContrast(foreground, background);
  const recommendations = getRecommendations();

  function calculateContrast(fg, bg) {
    const fgLuminance = getLuminance(fg);
    const bgLuminance = getLuminance(bg);
    return (Math.max(fgLuminance, bgLuminance) + 0.05) / (Math.min(fgLuminance, bgLuminance) + 0.05);
  }

  function getLuminance(hex) {
    const rgb = parseInt(hex.slice(1), 16);
    const r = (rgb >> 16) & 0xff;
    const g = (rgb >> 8) & 0xff;
    const b = (rgb >> 0) & 0xff;
    return (0.2126 * (r / 255) + 0.7152 * (g / 255) + 0.0722 * (b / 255));
  }

  function getRecommendations() {
    return [
      { fg: '#FFFFFF', bg: '#000000', ratio: calculateContrast('#FFFFFF', '#000000') },
      { fg: '#FF5733', bg: '#C70039', ratio: calculateContrast('#FF5733', '#C70039') },
      { fg: '#FFC300', bg: '#581845', ratio: calculateContrast('#FFC300', '#581845') },
    ];
  }

  const simulateColorBlindness = (hex) => {
    // A very basic simulation for demonstration
    const colorBlindTypes = {
      Protanopia: '#A0A0A0', // Simulated color for Protanopia
      Deuteranopia: '#A0A0A0', // Simulated color for Deuteranopia
      Tritanopia: '#A0A0A0', // Simulated color for Tritanopia
    };
    return colorBlindTypes[hex] || hex;
  };

  const savePalette = () => {
    const palette = {
      foreground,
      background,
      contrastRatio: contrastRatio.toFixed(2),
    };
    console.log('Palette saved:', palette);
    alert('Palette saved! Check the console for details.');
  };

  return (
    <section>
      <h2>Color Contrast Analyzer</h2>
      <input
        type="color"
        value={foreground}
        onChange={(e) => setForeground(e.target.value)}
      />
      <input
        type="color"
        value={background}
        onChange={(e) => setBackground(e.target.value)}
      />
      <p>Contrast Ratio: {contrastRatio.toFixed(2)}</p>
      <p>{contrastRatio >= 4.5 ? 'Good Contrast!' : 'Poor Contrast!'}</p>

      <h3>Accessibility Recommendations</h3>
      <ul>
        {recommendations.map((rec, index) => (
          <li key={index}>
            Foreground: <span style={{ color: rec.fg }}>{rec.fg}</span>, 
            Background: <span style={{ backgroundColor: rec.bg, color: '#fff' }}>{rec.bg}</span> - 
            Ratio: {rec.ratio.toFixed(2)}
          </li>
        ))}
      </ul>

      <h3>Color Blindness Simulation</h3>
      <p>Original Foreground: <span style={{ color: foreground }}>{foreground}</span></p>
      <p>Protanopia Simulation: <span style={{ color: simulateColorBlindness(foreground) }}>{simulateColorBlindness(foreground)}</span></p>
      <p>Deuteranopia Simulation: <span style={{ color: simulateColorBlindness(foreground) }}>{simulateColorBlindness(foreground)}</span></p>
      <p>Tritanopia Simulation: <span style={{ color: simulateColorBlindness(foreground) }}>{simulateColorBlindness(foreground)}</span></p>

      <button onClick={savePalette}>Save Palette</button>
    </section>
  );
};

export default ColorContrastAnalyzer;
