import React, { useState, useEffect } from 'react';
import './styles'

const KeyboardNavigator = () => {
  const [isPopupVisible, setPopupVisible] = useState(false);

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      setPopupVisible(true);
    }
  };

  const closePopup = () => {
    setPopupVisible(false);
  };

  // Close the popup when the Escape key is pressed
  const handleEscapePress = (event) => {
    if (event.key === 'Escape') {
      closePopup();
    }
  };

  useEffect(() => {
    if (isPopupVisible) {
      window.addEventListener('keydown', handleEscapePress);
    } else {
      window.removeEventListener('keydown', handleEscapePress);
    }

    return () => {
      window.removeEventListener('keydown', handleEscapePress);
    };
  }, [isPopupVisible]);

  return (
    <section>
      <h2>Keyboard Navigation Simulator</h2>
      <p>Practice navigating this example using the Tab key.</p>
      <div className="button-container">
        <button tabIndex="0" onKeyPress={handleKeyPress}>Button 1</button>
        <button tabIndex="0" onKeyPress={handleKeyPress}>Button 2</button>
        <button tabIndex="0" onKeyPress={handleKeyPress}>Button 3</button>
        <button tabIndex="0" onKeyPress={handleKeyPress}>Button 4</button>
        <button tabIndex="0" onKeyPress={handleKeyPress}>Button 5</button>
      </div>

      {isPopupVisible && (
        <div className="popup">
          <div className="popup-content">
            <span 
              className="close" 
              tabIndex="0" 
              onClick={closePopup} 
              onKeyPress={(e) => e.key === 'Enter' && closePopup()}
            >
              &times;
            </span>
            <h3>Congratulations!</h3>
            <p>You have learned how to navigate using the keyboard.</p>
            <p>Want to implement it? Head here to read more: <a href="https://webaim.org/techniques/keyboard/" target="_blank" rel="noopener noreferrer">WebAIM: Keyboard Accessibility</a></p>
          </div>
        </div>
      )}
    </section>
  );
};

export default KeyboardNavigator;
