import React, { useState } from 'react';
import './stUserFeedback.css';

const UserFeedback = () => {
  const [feedback, setFeedback] = useState('');
  const [rating, setRating] = useState(0);
  const [category, setCategory] = useState('');
  const [submittedFeedback, setSubmittedFeedback] = useState([]);
  const [isAnonymous, setIsAnonymous] = useState(false);
  const [showFollowUp, setShowFollowUp] = useState(false);
  const [followUpAnswer, setFollowUpAnswer] = useState('');

  const handleSubmit = () => {
    if (feedback) {
      const newFeedback = { text: feedback, rating, category, isAnonymous };
      setSubmittedFeedback([...submittedFeedback, newFeedback]);
      alert('Feedback submitted: ' + feedback);
      setFeedback('');
      setRating(0);
      setCategory('');
      setIsAnonymous(false);
      setShowFollowUp(true); // Show follow-up question after submission
    } else {
      alert('Please provide your feedback.');
    }
  };

  const handleFollowUpSubmit = () => {
    alert('Follow-up answer submitted: ' + followUpAnswer);
    setShowFollowUp(false);
    setFollowUpAnswer('');
  };

  return (
    <section className="feedback-section">
      <h2>User Feedback</h2>

      <div>
        <label>Category:</label>
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Select a category</option>
          <option value="Suggestion">Suggestion</option>
          <option value="Bug Report">Bug Report</option>
          <option value="Compliment">Compliment</option>
        </select>
      </div>

      <div>
        <label>
          <input
            type="checkbox"
            checked={isAnonymous}
            onChange={() => setIsAnonymous(!isAnonymous)}
          />
          Submit anonymously
        </label>
      </div>

      <div className="rating">
        <span>Rate your experience: </span>
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={`star ${rating >= star ? 'filled' : ''}`}
            onClick={() => setRating(star)}
          >
            ★
          </span>
        ))}
      </div>

      <textarea
        value={feedback}
        onChange={(e) => setFeedback(e.target.value)}
        placeholder="Share your feedback..."
      ></textarea>

      <button onClick={handleSubmit}>Submit Feedback</button>

      {showFollowUp && (
        <div className="follow-up">
          <h3>Follow-Up Question</h3>
          <textarea
            value={followUpAnswer}
            onChange={(e) => setFollowUpAnswer(e.target.value)}
            placeholder="Your response..."
          ></textarea>
          <button onClick={handleFollowUpSubmit}>Submit Follow-Up</button>
        </div>
      )}

      <div className="submitted-feedback">
        <h3>Recent Feedback</h3>
        <ul>
          {submittedFeedback.map((item, index) => (
            <li key={index}>
              <strong>Rating: {item.rating} ★</strong> - {item.text} 
              {item.isAnonymous ? " (Anonymous)" : ""}
              {item.category && <span> [Category: {item.category}]</span>}
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
};

export default UserFeedback;
