import React, { useState } from 'react';

const ResourceLibrary = () => {
  const [resources, setResources] = useState([
    { title: "Web Content Accessibility Guidelines (WCAG)", url: "https://www.w3.org/WAI/WCAG21/quickref/" },
    { title: "Color Contrast Checker", url: "https://webaim.org/resources/contrastchecker/" },
    { title: "A11Y Project", url: "https://a11yproject.com/" },
  ]);

  const [newResource, setNewResource] = useState({ title: '', url: '' });

  const handleAddResource = () => {
    if (newResource.title && newResource.url) {
      setResources([...resources, newResource]);
      setNewResource({ title: '', url: '' });
    }
  };

  return (
    <section>
      <h2>Resource Library</h2>
      <ul>
        {resources.map((resource, index) => (
          <li key={index}>
            <a href={resource.url} target="_blank" rel="noopener noreferrer">{resource.title}</a>
          </li>
        ))}
      </ul>
      <h3>Add a New Resource</h3>
      <input
        type="text"
        placeholder="Resource Title"
        value={newResource.title}
        onChange={(e) => setNewResource({ ...newResource, title: e.target.value })}
      />
      <input
        type="url"
        placeholder="Resource URL"
        value={newResource.url}
        onChange={(e) => setNewResource({ ...newResource, url: e.target.value })}
      />
      <button onClick={handleAddResource}>Add Resource</button>
    </section>
  );
};

export default ResourceLibrary;
