import React, { useEffect, useState } from 'react';
import './styles/CuriosityCorner.css';

const facts = [
  "1 in 5 people in the world live with a disability.",
  "Accessibility can improve usability for all users.",
  "Color blindness affects approximately 1 in 12 men and 1 in 200 women.",
];

const CuriosityCorner = () => {
  const [randomFact, setRandomFact] = useState('');
  const [isFactVisible, setIsFactVisible] = useState(false);
  const [bubbleAnimation, setBubbleAnimation] = useState(false);

  useEffect(() => {
    generateRandomFact();
  }, []);

  const generateRandomFact = () => {
    const fact = facts[Math.floor(Math.random() * facts.length)];
    setRandomFact(fact);
    setIsFactVisible(true);
    setBubbleAnimation(true);
    playSound();

    // Hide the fact after a set duration
    setTimeout(() => {
      setIsFactVisible(false);
      setBubbleAnimation(false);
    }, 5000); // 5 seconds to read the fact
  };

  const playSound = () => {
    const audio = new Audio('https://www.soundjay.com/button/sounds/button-1.mp3');
    audio.play().catch((error) => {
      console.error("Error playing sound:", error);
    });
  };

  return (
    <section className="curiosity-corner">
      <div className="background-animation"></div>
      <div className="globe-container" onClick={generateRandomFact}>
        <div className="globe"></div>
        {isFactVisible && (
          <div className={`bubble ${bubbleAnimation ? 'bubble-enter' : 'bubble-exit'}`}>
            {randomFact}
          </div>
        )}
      </div>
      <h2>Curiosity Corner</h2>
      <button className="fact-button" onClick={generateRandomFact}>Show Another Fact!</button>
    </section>
  );
};

export default CuriosityCorner;
