import React, { useState } from 'react';
import './styles/f]FontSizeAdjuster.css';

const FontSizeAdjuster = () => {
  const [fontSize, setFontSize] = useState(16);
  const [text, setText] = useState("Adjust this text!");
  const [textColor, setTextColor] = useState('#000000');
  const [fontFamily, setFontFamily] = useState('Arial');

  const resetSettings = () => {
    setFontSize(16);
    setText("Adjust this text!");
    setTextColor('#000000');
    setFontFamily('Arial');
  };

  return (
    <section>
      <h2>Font Size Adjuster</h2>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Enter your text here"
      />
      <div className="controls">
        <label>Font Size:</label>
        <input
          type="range"
          min="10"
          max="50"
          value={fontSize}
          onChange={(e) => setFontSize(e.target.value)}
        />
        <label>Text Color:</label>
        <input
          type="color"
          value={textColor}
          onChange={(e) => setTextColor(e.target.value)}
        />
        <label>Font Family:</label>
        <select
          value={fontFamily}
          onChange={(e) => setFontFamily(e.target.value)}
        >
          <option value="Arial">Arial</option>
          <option value="Courier New">Courier New</option>
          <option value="Georgia">Georgia</option>
          <option value="Times New Roman">Times New Roman</option>
          <option value="Verdana">Verdana</option>
        </select>
      </div>
      <p style={{ fontSize: `${fontSize}px`, color: textColor, fontFamily: fontFamily }}>
        {text}
      </p>
      <button onClick={resetSettings}>Reset</button>
    </section>
  );
};

export default FontSizeAdjuster;
